import "./agregarCarrito.css";
import React, { useState, useEffect } from "react";
import { Minus, Plus, Heart } from "lucide-react";
import { useParams, useNavigate } from "react-router-dom";
import imgProd1 from "../images/imgProd1.png";
import imgProd2 from "../images/imgProd2.png";
import imgProd3 from "../images/imgProd3.png";

// Base de datos simulada de productos
const productsDB = {
  1: {
    id: 1,
    name: "Manzana Roja Premium",
    price: 10.0,
    itemNumber: "1",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  2: {
    id: 2,
    name: "Arroz Blanco Embasado El Progreso 1.7 kg",
    price: 25.3,
    itemNumber: "2",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  3: {
    id: 3,
    name: "Leche Entera La Vaca Feliz 1L",
    price: 35.0,
    itemNumber: "3",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  4: {
    id: 4,
    name: "Pan Integral Artesanal",
    price: 15.0,
    itemNumber: "4",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  5: {
    id: 5,
    name: "Huevos de Granja AAA x12",
    price: 40.0,
    itemNumber: "5",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  6: {
    id: 6,
    name: "Pollo Fresco Entero",
    price: 80.0,
    itemNumber: "6",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  7: {
    id: 7,
    name: "Queso Fresco Nacional",
    price: 60.0,
    itemNumber: "7",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  8: {
    id: 8,
    name: "Yogurt Natural 500ml",
    price: 25.0,
    itemNumber: "8",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  9: {
    id: 9,
    name: "Cereal Integral con Miel",
    price: 45.0,
    itemNumber: "9",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
};

function AgregarCarrito() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);

  const frequentPurchases = [
    {
      name: "Arroz Blanco",
      price: 25.3,
      available: true,
      offer: true,
      image: imgProd1,
    },
    {
      name: "Frijoles",
      price: 40.5,
      available: false,
      offer: false,
      image: imgProd2,
    },
    {
      name: "AzÃºcar",
      price: 30,
      available: true,
      offer: true,
      image: imgProd3,
    },
  ];

  // Cargar producto basado en el ID
  useEffect(() => {
    const productData = productsDB[id];
    if (productData) {
      setProduct(productData);
    } else {
      navigate("/");
    }
  }, [id, navigate]);

  const incrementQuantity = () => setQuantity(quantity + 1);
  const decrementQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  // Loading state
  if (!product) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-100">
        <div className="text-xl text-gray-600">Cargando producto...</div>
      </div>
    );
  }

  return (
    <div className="bg-white-100 h-screen w-screen">
      {/* BotÃ³n de regreso */}
      <div className="p-4">
        <button
          onClick={() => navigate(-1)}
          className="bg-[#2b6daf] text-white px-4 py-2 rounded-lg hover:bg-[#1f4d7a] transition-colors"
        >
          â Volver
        </button>
      </div>

      {/* Layout principal con flex horizontal */}
      <div className="flex gap-6 p-6 h-full">
        {/* SecciÃ³n Compras Frecuentes - IZQUIERDA */}
        <div className="w-100 flex-shrink-0">
          <div className="bg-white rounded-lg shadow-sm p-6 h-full">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">
              Compras Frecuentes
            </h2>

            {/* Tarjetas estilo InicioUsuario */}
            <div className="flex flex-col gap-4 overflow-y-auto max-h-[calc(100vh-200px)]">
              {frequentPurchases.map((item, index) => (
                <div
                  key={index}
                  className="bg-white rounded-3xl p-3 shadow-lg border-2 border-transparent
                             w-[250px] h-[350px]"
                >
                  {/* Top row con badge y estrellas */}
                  <div className="flex justify-between items-center mb-1">
                    {item.offer && (
                      <span className="bg-[#2b6daf] text-white text-sm px-3 py-1 rounded-full">
                        Oferta
                      </span>
                    )}
                    <div className="text-[#2b6daf] text-xl">
                      <span>â</span>
                      <span>â</span>
                      <span className="text-gray-300">â</span>
                    </div>
                  </div>

                  {/* Imagen del producto */}
                  <div className="flex justify-center mt-2">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-20 h-20 object-contain"
                    />
                  </div>

                  {/* InformaciÃ³n del producto */}
                  <div className="mt-auto pt-4">
                    <h3 className="text-left text-lg font-medium mb-1">
                      {item.name}
                    </h3>
                    <div className="flex items-center justify-between mb-2">
                      <span
                        className={`text-sm font-medium ${
