                    <input
                      type="text"
                      placeholder="Precio"
                      className="input-field rounded-md bg-gray-100 p-2 border-gray-500 border-2"
                      value={precio}
                      onChange={(e) => setPrecio(e.target.value)}
                      required
                    />
                  </div>
