import "./App.css";
import React, { useState } from "react";
import Sidebar from "./sidebar";

function Menu() {
  const [showSidebar, setShowSidebar] = useState(false);
  const [moveButton, setLeft] = useState(false);
  const handleClick = () => {
    setShowSidebar(!showSidebar);
    setLeft(!moveButton);
  };

  return (
    <div className="h-screen flex">
      {showSidebar && <Sidebar />}

      <button
        onClick={handleClick}
        className={`btn_sidebar ${moveButton ? "left-[180px]" : "left-2"}`}
      >
        <span className="material-symbols-outlined text-[42px] text-white">
          menu
        </span>
      </button>

      <div className=" w-screen font-roboto text-4xl py-2 px-8">
        <h1 className=" text-center py-2"></h1>
        <div className=" grid grid-cols-1 sm:grid-cols-2 gap-2 py-3 px-8">
          <div className="orange_grid">
            <span class="material-symbols-outlined text-8xl text-white text-center md: text-9xl">
              calendar_meal
            </span>
            <h1 className="text-white text-2xl text-center md:text-3xl">
              Productos en buen estado:
            </h1>
          </div>
          <div className="orange_grid">
            <span class="material-symbols-outlined text-8xl text-white md: text-9xl ">
              chef_hat
            </span>
            <h1 className="text-white text-2xl md:text-3xl">
              Productos a transformar:
            </h1>
          </div>
          <div className="orange_grid">
            <span class="material-symbols-outlined text-8xl text-white md: text-9xl">
              shopping_cart_checkout
            </span>
            <h1 className="text-white text-2xl md:text-3xl">
              Raciones entregadas en la semana:
            </h1>
          </div>
          <div className="orange_grid">
            <span class="material-symbols-outlined text-8xl text-white md: text-9xl">
              local_shipping
            </span>
            <h1 className="text-white text-2xl md:text-3xl">
              Donaciones recibidas:
            </h1>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Menu;
