import React, { useState, useEffect } from "react";
import { Minus, Plus, Heart } from "lucide-react";
import { useParams, useNavigate } from "react-router-dom";
import { getAllProducts } from "../api/InventarioApi";

function AgregarCarrito() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [products, setProducts] = useState([]);

  // Cargar productos desde la API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await getAllProducts();
        setProducts(res.data);

        const found = res.data.find(
          (p) => String(p.id_producto) === String(id)
        );
        if (found) {
          setProduct(found);
        } else {
          navigate("/");
        }
      } catch (err) {
        console.error("[PRODUCTS] error:", err?.response?.data || err);
        alert(err?.response?.data?.message || "Error al cargar productos");
      }
    };
    console.log(res.data);

    fetchProducts();
  }, [id, navigate]);

  const incrementQuantity = () => setQuantity(quantity + 1);
  const decrementQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  const handleFrequentProductClick = (productId) => {
    navigate(`/producto/${productId}`);
  };

  if (!product) {
    return (
      <div style={styles.loadingWrapper}>
        <div style={styles.loadingText}>Cargando producto...</div>
      </div>
    );
  }

  return (
    <div style={styles.pageWrapper}>
      <div style={styles.container}>
        {/* Compras frecuentes */}
        <div style={styles.sidebar}>
          <h2 style={styles.sidebarTitle}>Compras Frecuentes</h2>

          <div style={styles.frequentList}>
            {products.map((p, index) => (
              <div
                key={index}
                style={styles.frequentCard}
                onClick={() => handleFrequentProductClick(p.id_producto)}
              >
                <div style={styles.cardTopRow}>
                  {p.oferta && <span style={styles.badge}>Oferta </span>}
                  <div style={styles.stars}>
                    {Array.from({ length: 5 }, (_, i) => (
                      <span
                        key={i}
                        style={{
                          color:
                            i < Math.round(p.estrellas) ? "#2b6daf" : "#ddd",
                        }}
                      >
                        ★
                      </span>
                    ))}
                  </div>
                </div>

                <div style={styles.cardImageWrapper}>
                  <img src={p.img} alt={p.nombre} style={styles.cardImage} />
                </div>

                <div style={styles.cardContent}>
                  <h3 style={styles.cardTitle}>{p.nombre}</h3>

                  <p style={styles.cardPrice}>L. {p.precio_base} P/Kilo</p>

                  <button
                    style={styles.addButton}
                    onClick={(e) => {
                      e.stopPropagation();
                      // lógica carrito
                    }}
                  >
                    Agregar
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Detalle del producto */}
        <div style={styles.detailWrapper}>
          <div style={styles.detailCard}>
            <div style={styles.detailGrid}>
              {/* Imagen */}
              <div style={styles.detailImageWrapper}>
                <img
                  src={product.img}
                  alt={product.nombre}
                  style={styles.detailImage}
                />
              </div>

              {/* Info */}
              <div style={styles.detailInfo}>
                <h1 style={styles.detailTitle}>{product.nombre}</h1>
                <p style={styles.detailCode}>Código: {product.id_producto}</p>

                <div style={styles.detailStockWrapper}>
                  <div style={styles.detailPrice}>
                    L. {product.precio_base}{" "}
                    <span style={styles.detailPriceUnit}>P/Kilo</span>
                  </div>
                </div>

                <div style={styles.actionsRow}>
                  <div style={styles.quantityWrapper}>
                    <button style={styles.qtyBtn} onClick={decrementQuantity}>
                      <Minus size={20} />
                    </button>
                    <span style={styles.qtyText}>{quantity}</span>
                    <button style={styles.qtyBtn} onClick={incrementQuantity}>
                      <Plus size={20} />
                    </button>
                  </div>

                  <button
                    style={styles.cartBtn}
                    onClick={() => {
                      // lógica carrito
                    }}
                  >
                    AGREGAR AL CARRITO
                  </button>

                  <button
                    style={styles.favoriteBtn}
                    onClick={() => {
                      // lógica favoritos
                    }}
                  >
                    <Heart size={24} fill="white" />
                  </button>
                </div>
                <div style={styles.detailDescription}>
                  Aqui es donde va la descripcion del producto. Lorem ipsum
                  dolor sit amet, consectetur adipiscing elit. Quisque nisl
                  eros, pulvinar facilisis justo mollis, auctor consequat urna.
                  Morbi a bibendum metus. Donec scelerisque sollicitudin enim eu
                  venenatis. Duis tincidunt laoreet ex, in pretium orci
                  vestibulum eget.
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  pageWrapper: {
    position: "absolute",
    top: "145px",
    left: 0,
    right: 0,
    width: "100%",
    height: "calc(100% - 145px)",
    display: "flex",
    flexDirection: "column",
  },
  container: {
    padding: "20px",
    margin: "0 auto",
    display: "flex",
    gap: "20px",
    width: "100%",
    maxWidth: "1400px",
  },
  loadingWrapper: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#f5f5f5",
  },
  loadingText: {
    fontSize: "20px",
    color: "#666",
  },
  sidebar: {
    flexShrink: 0,
    width: "280px",
    backgroundColor: "white",
    borderRadius: "16px",
    padding: "20px",
    boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
    height: "100vh",
    overflowY: "auto",
  },

  sidebarTitle: {
    fontSize: "20px",
    fontWeight: "600",
    marginBottom: "16px",
  },
  frequentList: {
    display: "flex",
    flexDirection: "column",
    gap: "12px",
  },
  frequentCard: {
    backgroundColor: "white",
    borderRadius: "20px",
    padding: "12px",
    boxShadow: "0px 4px 10px rgba(0,0,0,0.1)",
    cursor: "pointer",
    transition: "all 0.2s ease-in-out",
  },
