import "./agregarCarrito.css";
import React, { useState } from "react";
import { Minus, Plus, Heart } from "lucide-react";
import imgProd1 from "../images/imgProd1.png";
import imgProd2 from "../images/imgProd2.png";
import imgProd3 from "../images/imgProd3.png";

function AgregarCarrito() {
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);

  const productImages = [imgProd1, imgProd2, imgProd3];
  const frequentPurchases = [
    {
      name: "Arroz Blanco",
      price: 25.3,
      available: true,
      offer: true,
      image: imgProd1,
    },
    {
      name: "Frijoles",
      price: 40.5,
      available: false,
      offer: false,
      image: imgProd2,
    },
    {
      name: "Azúcar",
      price: 30,
      available: true,
      offer: true,
      image: imgProd3,
    },
  ];

  const incrementQuantity = () => setQuantity(quantity + 1);
  const decrementQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  return (
    <div className="bg-white-100 h-screen w-screen">
      {/* Layout principal con flex horizontal */}
      <div className="flex gap-6 p-6 h-full">
        {/* Sección Compras Frecuentes - IZQUIERDA */}
        <div className="w-100 flex-shrink-0">
          <div className="bg-white rounded-lg shadow-sm p-6 h-full">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">
              Compras Frecuentes
            </h2>

            {/* Tarjetas estilo InicioUsuario */}
            <div className="flex flex-col gap-4 overflow-y-auto max-h-[calc(100vh-200px)]">
              {frequentPurchases.map((item, index) => (
                <div
                  key={index}
                  className="bg-white rounded-3xl p-3 shadow-lg border-2 border-transparent
                             w-[250px] h-[350px]"
                >
                  {/* Top row con badge y estrellas */}
                  <div className="flex justify-between items-center mb-1">
                    {item.offer && (
                      <span className="bg-[#2b6daf] text-white text-sm px-3 py-1 rounded-full">
                        Oferta
                      </span>
                    )}
                    <div className="text-[#2b6daf] text-xl">
                      <span>★</span>
                      <span>★</span>
                      <span className="text-gray-300">☆</span>
                    </div>
                  </div>

                  {/* Imagen del producto */}
                  <div className="flex justify-center mt-2">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-20 h-20 object-contain"
                    />
                  </div>

                  {/* Información del producto */}
                  <div className="mt-auto pt-4">
                    <h3 className="text-left text-lg font-medium mb-1">
                      {item.name}
                    </h3>
                    <div className="flex items-center justify-between mb-2">
                      <span
                        className={`text-sm font-medium ${
                          item.available ? "text-green-600" : "text-red-600"
                        }`}
                      >
                        {item.available ? "DISPONIBLE" : "NO DISPONIBLE"}
                      </span>
                    </div>
                    <p className="text-left text-base text-gray-600 mb-3">
                      L. {item.price}/Kilo
                    </p>

                    {/* Botón agregar */}
                    <button className="w-full bg-[#F0833E] hover:bg-[#2b6daf] text-white py-2 rounded-full font-medium transition-colors duration-200 text-lg border border-[#D8572F]">
                      Agregar
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Producto Principal - DERECHA */}
        <div className="flex-1">
          <div className="bg-white rounded-lg shadow-sm p-6 h-full">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 h-full">
              {/* Imágenes del producto */}
              <div>
                <div className="mb-4">
                  <img
                    src={productImages[selectedImage]}
                    alt="Arroz Blanco Embasado El Progreso"
                    className="w-full h-80 object-contain border rounded-lg"
                  />
                </div>
                <div className="flex space-x-2 justify-center">
                  {productImages.map((_, index) => (
                    <button
                      key={index}
                      onClick={() => setSelectedImage(index)}
                      className={`w-16 h-16 border-2 rounded ${
                        selectedImage === index
                          ? "border-orange-500"
                          : "border-gray-200"
                      }`}
                    >
                      <img
                        src={productImages[index]}
                        alt={`Vista ${index + 1}`}
                        className="w-full h-full object-contain"
                      />
                    </button>
                  ))}
                </div>
              </div>

              {/* Información del producto */}
              <div className="w-full text-left">
                <h1 className="text-3xl font-bold text-gray-800 mb-2">
                  Arroz Blanco Embasado El Progreso 1.7 kg
                </h1>
                <p className="text-sm text-gray-600 mb-4">Número Item: 2</p>

                <div className="mb-6">
                  <span className="text-green-600 font-semibold">
                    DISPONIBLE
                  </span>
                  <div className="text-3xl font-bold text-gray-800 mt-2">
                    L. 25.30 <span className="text-lg font-normal">P/Kilo</span>
                  </div>
                </div>

                {/* Cantidad, Botón Agregar y Me Gusta en la misma línea */}
                <div className="flex items-center gap-4 mb-6">
                  {/* Controles de cantidad */}
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={decrementQuantity}
                      className="w-10 h-10 rounded-full border-2 border-[#2B6DAF] bg-[#2B6DAF] text-white flex items-center justify-center hover:bg-[#1f4d7a]"
                    >
                      <Minus size={20} />
                    </button>
                    <span className="text-2xl font-semibold w-8 text-center">
                      {quantity}
                    </span>
                    <button
                      onClick={incrementQuantity}
                      className="w-10 h-10 rounded-full border-2 border-[#2B6DAF] bg-[#2B6DAF] text-white flex items-center justify-center hover:bg-[#1f4d7a]"
                    >
                      <Plus size={20} />
                    </button>
                  </div>

                  {/* Botón Agregar al Carrito */}
                  <button className="flex-1 bg-orange-500 text-white py-3 px-6 rounded-[25px] font-semibold hover:bg-orange-600 transition-colors">
                    AGREGAR AL CARRITO
                  </button>

                  {/* Botón Me Gusta */}
                  <button className="bg-orange-500 text-orange-500 py-3 px-6 rounded-[25px] font-semibold hover:bg-[#1f4d7a] transition-colors">
                    <Heart size={24} />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AgregarCarrito;
