import "./agregarCarrito.css";
import React, { useState } from "react";
import { Minus, Plus } from "lucide-react";
import imgProd1 from "../images/imgProd1.png";
import imgProd2 from "../images/imgProd2.png";
import imgProd3 from "../images/imgProd3.png";

function AgregarCarrito() {
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);

  const productImages = [imgProd1, imgProd2, imgProd3];
  const frequentPurchases = [
    {
      name: "Arroz Blanco",
      price: 25.3,
      available: true,
      offer: true,
      image: imgProd1,
    },
    {
      name: "Frijoles",
      price: 40.5,
      available: false,
      offer: false,
      image: imgProd2,
    },
    {
      name: "Azúcar",
      price: 30,
      available: true,
      offer: true,
      image: imgProd3,
    },
  ];

  const incrementQuantity = () => setQuantity(quantity + 1);
  const decrementQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  return (
    <div className="bg-gray-100 h-screen w-screen">
      {/* Sección Compras Frecuentes */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">
          Compras Frecuentes
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {frequentPurchases.map((item, index) => (
            <div key={index} className="border rounded-lg p-4 relative">
              {item.offer && (
                <div className="absolute top-2 left-2 bg-red-500 text-white text-xs px-2 py-1 rounded">
                  OFERTA
                </div>
              )}
              <img
                src={item.image}
                alt={item.name}
                className="w-full h-32 object-contain mb-3"
              />
              <h3 className="font-medium text-sm mb-2">{item.name}</h3>
              <div className="flex items-center justify-between mb-3">
                <span
                  className={`text-sm ${
                    item.available ? "text-green-600" : "text-red-600"
                  }`}
                >
                  {item.available ? "DISPONIBLE" : "NO DISPONIBLE"}
                </span>
                <span className="font-bold">L. {item.price}/Kilo</span>
              </div>
              <button className="w-full bg-orange-500 text-white py-2 rounded font-medium hover:bg-orange-600 transition-colors">
                AGREGAR AL CARRITO
              </button>
            </div>
          ))}
        </div>
      </div>
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Imágenes del producto */}
          <div>
            <div className="mb-4">
              <img
                src={productImages[selectedImage]}
                alt="Arroz Blanco Embasado El Progreso"
                className="w-full h-80 object-contain border rounded-lg"
              />
            </div>
            <div className="flex space-x-2 justify-center">
              {productImages.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setSelectedImage(index)}
                  className={`w-16 h-16 border-2 rounded ${
                    selectedImage === index
                      ? "border-orange-500"
                      : "border-gray-200"
                  }`}
                >
                  <img
                    src={productImages[index]}
                    alt={`Vista ${index + 1}`}
                    className="w-full h-full object-contain"
                  />
                </button>
              ))}
            </div>
          </div>

          {/* Información del producto */}
          <div className="w-full text-left">
            <h1 className="text-3xl font-bold text-gray-800 mb-2">
              Arroz Blanco Embasado El Progreso 1.7 kg
            </h1>
            <p className="text-sm text-gray-600 mb-4">Número Item: 2</p>

            <div className="mb-6">
              <span className="text-green-600 font-semibold">DISPONIBLE</span>
              <div className="text-3xl font-bold text-gray-800 mt-2">
                L. 25.30 <span className="text-lg font-normal">P/Kilo</span>
              </div>
            </div>

            {/* Cantidad */}
            <div className="flex items-center space-x-4 mb-6">
              <button
                onClick={decrementQuantity}
                className="w-10 h-10 rounded-full border-2 border-[#2B6DAF] bg-[#2B6DAF] text-white flex items-center justify-center hover:bg-[#1f4d7a]"
              >
                <Minus size={20} />
              </button>
              <span className="text-2xl font-semibold w-8 text-center">
                {quantity}
              </span>
              <button
                onClick={incrementQuantity}
                className="w-10 h-10 rounded-full border-2 border-[#2B6DAF] bg-[#2B6DAF] text-white flex items-center justify-center hover:bg-[#1f4d7a]"
              >
                <Plus size={20} />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AgregarCarrito;
