import React, { useRef, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { EffectCoverflow, Autoplay } from "swiper/modules";
import Sidebar from "../sidebar";
import "swiper/css";
import "swiper/css/effect-coverflow";
import "swiper/css/autoplay";
import { useNavigate } from "react-router-dom";

import carrot from "../images/carrot.png";
import apple from "../images/apple.png";
import pet from "../images/pet.png";
import ham from "../images/ham.png";
import cake from "../images/cake.png";
import bread from "../images/bread.png";
import juice from "../images/juice.png";
import clean from "../images/clean.png";
import soccer from "../images/soccer.png";
import phone from "../images/phone.png";
import pharmacy from "../images/pharmacy.png";
import milk from "../images/milk.png";
import arrowL from "../images/arrowL.png";
import arrowR from "../images/arrowR.png";
import appleImage from "../images/appleImage.png";
import coffee from "../images/coffee.png";
import banner1 from "../images/banner1.png";
import banner2 from "../images/banner2.png";
import banner3 from "../images/banner3.png";

const categories = [
  { name: "Lácteos", icon: milk },
  { name: "Farmacia", icon: pharmacy },
  { name: "Electrónico", icon: phone },
  { name: "Deportes", icon: soccer },
  { name: "Limpieza", icon: clean },
  { name: "Bebidas", icon: juice },
  { name: "Panadería", icon: bread },
  { name: "Repostería", icon: cake },
  { name: "Embutidos", icon: ham },
  { name: "Mascotas", icon: pet },
  { name: "Frutas", icon: apple },
  { name: "Verduras", icon: carrot },
  { name: "Granos", icon: coffee },
];

export const products = [
  { name: "Manzana", price: "L. 10.00", img: appleImage },
  { name: "Arroz Blanco", price: "L. 25.30", img: appleImage },
  { name: "Frijoles", price: "L. 40.50", img: appleImage },
];

const banners = [banner1, banner3, banner2];

const InicioAdmin = () => {
  const catRef = useRef(null);
  const prodRefDestacados = useRef(null);
  const prodRefTendencias = useRef(null);
  const [moveButton, setLeft] = useState(false);
  const [showSidebar, setShowSidebar] = useState(false);
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [hoveredProductDest, setHoveredProductDest] = useState(null);
  const [hoveredProductTrend, setHoveredProductTrend] = useState(null);

  const handleClick = () => {
    setLeft(!moveButton);
    setShowSidebar(!showSidebar);
  };

  const scroll = (direction, ref, itemWidth) => {
    if (ref.current) {
      ref.current.scrollBy({
        left: direction === "left" ? -itemWidth : itemWidth,
        behavior: "smooth",
      });
    }
  };

  const navigate = useNavigate();

  return (
    <div style={{ ...styles.fixedShell, backgroundColor: "#F5F5F5" }}>
      <div>
        {showSidebar && <Sidebar />}
        <button
          onClick={handleClick}
          className={`btn_sidebar ${moveButton ? "left-[186px]" : "left-2"}`}
        >
          <span className="material-symbols-outlined text-[42px] text-white">
            menu
          </span>
        </button>
      </div>

      {/* Categorías */}
      <div className="mt-4" style={styles.scrollWrapper}>
        <button
          style={styles.arrow}
          onClick={() => scroll("left", catRef, 140)}
        >
          <img src={arrowL} alt="left" />
        </button>

        <div style={styles.divCat} ref={catRef}>
          {categories.map((cat, index) => (
            <div key={index} style={styles.catItem}>
              <div
                style={{
                  ...styles.CategoriesBox,
                  border:
                    hoveredIndex === index
                      ? "2px solid #2b6daf"
                      : "2px solid transparent",
                  transform:
                    hoveredIndex === index ? "scale(1.05)" : "scale(1)",
                  transition: "all 0.2s ease-in-out",
                }}
                onMouseEnter={() => setHoveredIndex(index)}
                onMouseLeave={() => setHoveredIndex(null)}
              >
                <img src={cat.icon} alt={cat.name} />
              </div>
              <span style={styles.catTitle}>{cat.name}</span>
            </div>
          ))}
        </div>

        <button
          style={styles.arrow}
          onClick={() => scroll("right", catRef, 140)}
        >
          <img src={arrowR} alt="right" />
        </button>
      </div>

      {/* Banner */}
      <div style={{ margin: "40px 0" }}>
        <Swiper
          modules={[EffectCoverflow, Autoplay]}
          effect="coverflow"
          slidesPerView={3}
          loop
          autoplay={{ delay: 3000 }}
        >
          {banners.map((banner, index) => (
            <SwiperSlide key={index}>
              <img
                src={banner}
                alt={`banner-${index}`}
                style={{
                  width: "100%",
                  height: "300px",
                  borderRadius: "16px",
                  objectFit: "cover",
                }}
              />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>

      {/* Productos Destacados */}
      <h2 style={styles.sectionTitle}>Productos Destacados</h2>
      <div style={styles.scrollWrapper}>
        <div style={styles.divProducts} ref={prodRefDestacados}>
          {products.map((p, i) => (
            <div
              key={i}
              style={{
                ...styles.productBox,
                border:
                  hoveredProductDest === i
                    ? "2px solid #2b6daf"
                    : "2px solid transparent",
              }}
              onMouseEnter={() => setHoveredProductDest(i)}
              onMouseLeave={() => setHoveredProductDest(null)}
            >
              <img src={p.img} alt={p.name} style={styles.productImg} />
              <p>{p.name}</p>
              <p>{p.price}</p>
              <button
                style={{
                  ...styles.addButton,
                  backgroundColor:
                    hoveredProductDest === i ? "#2b6daf" : "#F0833E",
                }}
                onClick={() => navigate(`/AgregarCarrito/${i}`)}
              >
                Agregar
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const styles = {
  fixedShell: { position: "absolute", top: "145px", width: "100%" },
  scrollWrapper: { display: "flex", alignItems: "center", width: "100%" },
  divCat: { display: "flex", gap: "10px", overflowX: "auto" },
  catItem: { display: "flex", flexDirection: "column", alignItems: "center" },
  CategoriesBox: {
    background: "white",
    borderRadius: "25px",
    width: "90px",
    height: "90px",
  },
  catTitle: { marginTop: "8px", fontSize: "14px" },
  arrow: { background: "transparent", width: "35px", height: "35px" },
  divProducts: { display: "flex", gap: "13px", overflowX: "auto" },
  productBox: {
    width: "249px",
    height: "265px",
    borderRadius: "25px",
    background: "#fff",
  },
  productImg: { width: "90px", height: "90px", objectFit: "contain" },
  addButton: { width: "100%", borderRadius: "25px", padding: "6px" },
  sectionTitle: { fontSize: "22px", fontWeight: "650", padding: "10px" },
};

export default InicioAdmin;
