import React, { useState } from "react";
import { Minus, Plus } from "lucide-react";
import "./agregarCarrito.css";

import imgProd1 from "../images/imgProd1.png";
import imgProd2 from "../images/imgProd2.png";
import imgProd3 from "../images/imgProd3.png";

function AgregarCarrito() {
  const [cantidad, setCantidad] = useState(1);
  const [selectedImage, setSelectedImage] = useState(imgProd1);

  const increase = () => setCantidad(cantidad + 1);
  const decrease = () => {
    if (cantidad > 1) setCantidad(cantidad - 1);
  };

  return (
    <div className="page-container">
      <div className="content">
        {/* Columna izquierda */}
        <div className="left-column">
          <div className="frequent-container">
            <h2 className="title">Compras Frecuentes</h2>
            <div className="product-list">
              {[imgProd1, imgProd2, imgProd3].map((img, index) => (
                <div
                  key={index}
                  className="product-card"
                  onClick={() => setSelectedImage(img)}
                >
                  <img src={img} alt={`Producto ${index + 1}`} />
                  <p>Producto {index + 1}</p>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Columna derecha */}
        <div className="right-column">
          <div className="image-container">
            <img src={selectedImage} alt="Producto seleccionado" />
          </div>

          <div className="info-container">
            <h2>Nombre del Producto</h2>
            <p className="price">L. 200.00</p>

            <div className="quantity">
              <button onClick={decrease}>
                <Minus size={20} />
              </button>
              <span>{cantidad}</span>
              <button onClick={increase}>
                <Plus size={20} />
              </button>
            </div>

            <button className="add-button">Agregar al carrito</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AgregarCarrito;
