import "./agregarCarrito.css";
import React, { useState, useEffect } from "react";
import { Minus, Plus, Heart } from "lucide-react";
import { useParams, useNavigate } from "react-router-dom";
import { getAllProducts } from "../api/InventarioApi";

function AgregarCarrito() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);

  const [products, setProducts] = useState([]);

  // Cargar productos desde la API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await getAllProducts();
        setProducts(res.data);

        // Buscar el producto por id
        const found = res.data.find(
          (p) => String(p.id_producto) === String(id)
        );
        if (found) {
          setProduct(found);
        } else {
          navigate("/");
        }
      } catch (err) {
        console.error("[PRODUCTS] error:", err?.response?.data || err);
        alert(err?.response?.data?.message || "Error al cargar productos");
      }
    };

    fetchProducts();
  }, [id, navigate]);

  const incrementQuantity = () => setQuantity(quantity + 1);
  const decrementQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  const handleFrequentProductClick = (productId) => {
    navigate(`/producto/${productId}`);
  };

  if (!product) {
    return (
      <div className="flex ps-center justify-center h-screen bg-gray-100">
        <div className="text-xl text-gray-600">Cargando producto...</div>
      </div>
    );
  }

  return (
    <div className="bg-gray-100 h-screen w-screen position absolute top-36 left-0 p-6">
      <div className="flex gap-6 p-6 h-full">
        {/* Compras frecuentes */}
        <div className="w-100 flex-shrink-0">
          <div className="bg-white rounded-lg shadow-sm p-6 h-full">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">
              Compras Frecuentes
            </h2>

            <div className="flex flex-col gap-4 overflow-y-auto h-[650px] pr-2 position relative">
              {products.map((p, index) => (
                <div
                  key={index}
                  className="bg-white rounded-3xl p-3 shadow-lg border-2 border-transparent
                             w-[250px] h-[300px] cursor-pointer hover:scale-105 
                             transition-all duration-200"
                  onClick={() => handleFrequentProductClick(p.id_producto)}
                >
                  <div className="flex justify-between ps-center mb-1">
                    {p.oferta && (
                      <span className="bg-[#2b6daf] text-white text-sm px-3 py-1 rounded-full">
                        Oferta
                      </span>
                    )}
                    <div className="text-[#2b6daf] text-xl">
                      {Array.from({ length: 5 }, (_, i) => (
                        <span
                          key={i}
                          className={
                            i < Math.round(p.estrellas)
                              ? "text-[#2b6daf]"
                              : "text-gray-300"
                          }
                        >
                          ‚òÖ
                        </span>
                      ))}
                    </div>
                  </div>

                  <div className="flex justify-center mt-2">
                    <img
                      src={p.img}
                      alt={p.nombre}
                      className="w-20 h-20 object-contain"
                    />
                  </div>

                  <div className="mt-auto pt-4">
                    <h3 className="text-left text-lg font-medium mb-1">
                      {p.nombre}
                    </h3>
                    <div className="flex ps-center justify-between mb-2">
                      <span
                        className={`text-sm font-medium ${
                          p.activo ? "text-green-600" : "text-red-600"
                        }`}
                      >
                        {p.activo ? "DISPONIBLE" : "NO DISPONIBLE"}
                      </span>
                    </div>
                    <p className="text-left text-base text-gray-600 mb-3">
                      L. {p.precio_base} P/Kilo
                    </p>

                    <button
                      className="w-full bg-[#F0833E] hover:bg-[#2b6daf] text-white py-2 rounded-full font-medium transition-colors duration-200 text-lg border border-[#D8572F]"
                      onClick={(e) => {
                        e.stopPropagation();
                        // l√≥gica para agregar al carrito
                      }}
                    >
                      Agregar
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Detalle del producto */}
        <div className="flex-1">
          <div className="bg-white rounded-lg shadow-sm p-6 h-full">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 h-full">
              {/* Imagen principal */}
              <div className="flex flex-col ps-center border rounded-lg justify-center p-4">
                <div className="mb-4">
                  <img
                    src={product.img}
                    alt={product.nombre}
                    className="w-full h-80 object-contain border rounded-lg"
                  />
                </div>
              </div>

              {/* Info */}
              <div className="w-full text-left justify-center flex flex-col">
                <h1 className="text-3xl font-bold text-gray-800 mb-2">
                  {product.nombre}
                </h1>
                <p className="text-sm text-gray-600 mb-4">
                  C√≥digo: {product.id_producto}
                </p>

                <div className="mb-6">
                  <span
                    className={`font-semibold ${
                      product.activo ? "text-green-600" : "text-red-600"
                    }`}
                  >
                    {product.activo ? "DISPONIBLE" : "NO DISPONIBLE"}
                  </span>
                  <div className="text-3xl font-bold text-gray-800 mt-2">
                    L. {product.precio_base}{" "}
                    <span className="text-lg font-normal">P/Kilo</span>
                  </div>
                </div>

                <div className="flex ps-center gap-4 mb-6">
                  <div className="flex ps-center space-x-2">
                    <button
                      onClick={decrementQuantity}
                      className="w-10 h-10 rounded-full border-2 border-[#2B6DAF] bg-[#2B6DAF] text-white flex ps-center justify-center hover:bg-[#1f4d7a]"
                    >
                      <Minus size={20} />
                    </button>
                    <span className="text-2xl font-semibold w-8 text-center">
                      {quantity}
                    </span>
                    <button
                      onClick={incrementQuantity}
                      className="w-10 h-10 rounded-full border-2 border-[#2B6DAF] bg-[#2B6DAF] text-white flex ps-center justify-center hover:bg-[#1f4d7a]"
                    >
                      <Plus size={20} />
                    </button>
                  </div>

                  <button
                    className="flex-1 bg-orange-500 text-white py-3 px-6 rounded-[25px] font-semibold hover:bg-orange-600 transition-colors"
                    onClick={() => {
                      // l√≥gica para agregar al carrito
                    }}
                  >
                    AGREGAR AL CARRITO
                  </button>

                  <button
                    className="bg-red-500 text-white py-3 px-6 rounded-[25px] font-semibold border-2 border-red-500 hover:bg-red-600 transition-colors"
                    onClick={() => {
                      // l√≥gica para favoritos
                    }}
                  >
                    <Heart size={24} fill="white" />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AgregarCarrito;
