import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./login.css";
import LoginUser from "./api/Usuario.Route"; // export default que hace POST /api/auth/login

const Login = () => {
  const [correo, setCorreo] = useState("");
  const [contrasena, setContrasena] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!correo || !contrasena) {
      return alert("Ingresa correo y contraseña.");
    }

    try {
      setLoading(true);
      // tu backend acepta 'contraseña' o 'contrasena'
      const res = await LoginUser({ correo, contraseña: contrasena });
      console.log("LOGIN OK:", res.data);
      // si usas cookie httpOnly, ya quedó guardada automáticamente (withCredentials: true)
      navigate("/"); // redirige a inicio (ajusta ruta si quieres)
    } catch (err) {
      console.error(err?.response?.data || err);
      alert(err?.response?.data?.message || "Error de login");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-form">
      <img className="logo-titulo" src="/logo-easyway.jpg" alt="Logo" />
      <p className="inicio-sesion-text">Inicio de Sesión</p>
      <p className="facil-text">Inicia sesión para comprar fácil, rápido y seguro</p>

      <hr className="linea" />

      <form onSubmit={onSubmit}>
        <div className="input-wrapper">
          <input
            type="email"
            className="input-field"
            placeholder="Correo electrónico"
            value={correo}
            onChange={(e) => setCorreo(e.target.value)}
            autoComplete="email"
            required
          />

          <input
            type="password"
            className="input-field"
            placeholder="Contraseña"
            value={contrasena}
            onChange={(e) => setContrasena(e.target.value)}
            autoComplete="current-password"
            required
            minLength={4}
          />
        </div>

        <Link to="./forgot_password" className="forgot-pass-link" rel="noopener noreferrer">
          ¿Olvidaste tu contraseña?
        </Link>

        <button className="login-button" type="submit" disabled={loading}>
          {loading ? "Ingresando..." : "Inicia Sesión"}
        </button>
      </form>

      <Link to="./crear_cuenta" className="new-link" rel="noopener noreferrer">
        ¿Nuevo aquí?
      </Link>
    </div>
  );
};

export default Login;
