import React, { useState } from "react";
import "./agregarCarrito.css";
import imgProd1 from "../images/imgProd1.png";
import imgProd2 from "../images/imgProd2.png";
import imgProd3 from "../images/imgProd3.png";

function AgregarCarrito() {
  const [cantidad, setCantidad] = useState(1);

  return (
    <div className="bg-gray-100 h-screen w-screen flex items-center justify-center p-6">
      <div className="bg-white rounded-2xl shadow-xl p-6 flex max-w-5xl w-full">
        {/* Imagenes del producto */}
        <div className="flex flex-col items-center w-1/3">
          <img src={imgProd1} alt="Producto" className="rounded-lg shadow-md" />
          <div className="flex space-x-2 mt-4">
            <img
              src={imgProd2}
              alt="Producto"
              className="rounded-lg shadow-md"
            />
          </div>
          <img src={imgProd3} alt="Producto" className="rounded-lg shadow-md" />
          <img src={imgProd2} alt="Producto" className="rounded-lg shadow-md" />
        </div>

        {/* Información del producto */}
        <div className="w-2/3 pl-6 flex flex-col justify-between">
          <div>
            <h2 className="text-xl font-bold text-gray-800">
              Arroz Blanco Embazado El Progreso 1.7 kg
            </h2>
            <p className="text-green-600 font-semibold mt-2">DISPONIBLE</p>
            <p className="text-lg font-medium mt-2">L. 25.30 P/Kilo</p>
          </div>

          {/* Controles de cantidad y botón */}
          <div className="flex items-center mt-6">
            <button
              onClick={() => setCantidad(cantidad > 1 ? cantidad - 1 : 1)}
              className="px-3 py-1 bg-gray-200 rounded-full text-lg"
            >
              -
            </button>
            <span className="px-4 text-lg">{cantidad}</span>
            <button
              onClick={() => setCantidad(cantidad + 1)}
              className="px-3 py-1 bg-gray-200 rounded-full text-lg"
            >
              +
            </button>
            <button className="ml-4 px-6 py-2 bg-red-500 hover:bg-red-600 text-white rounded-full font-semibold">
              AGREGAR AL CARRITO
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AgregarCarrito;
