import "./agregarCarrito.css";
import React, { useState, useEffect } from "react";
import { Minus, Plus, Heart } from "lucide-react";
import { useParams, useNavigate } from "react-router-dom";
import imgProd1 from "../images/imgProd1.png";
import imgProd2 from "../images/imgProd2.png";
import imgProd3 from "../images/imgProd3.png";

const productsDB = {
  1: {
    id: 1,
    name: "Manzana Roja Premium",
    price: 10.0,
    itemNumber: "1",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  2: {
    id: 2,
    name: "Arroz Blanco Embasado El Progreso 1.7 kg",
    price: 25.3,
    itemNumber: "2",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  3: {
    id: 3,
    name: "Leche Entera La Vaca Feliz 1L",
    price: 35.0,
    itemNumber: "3",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  4: {
    id: 4,
    name: "Pan Integral Artesanal",
    price: 15.0,
    itemNumber: "4",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  5: {
    id: 5,
    name: "Huevos de Granja AAA x12",
    price: 40.0,
    itemNumber: "5",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  6: {
    id: 6,
    name: "Pollo Fresco Entero",
    price: 80.0,
    itemNumber: "6",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  7: {
    id: 7,
    name: "Queso Fresco Nacional",
    price: 60.0,
    itemNumber: "7",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  8: {
    id: 8,
    name: "Yogurt Natural 500ml",
    price: 25.0,
    itemNumber: "8",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
  9: {
    id: 9,
    name: "Cereal Integral con Miel",
    price: 45.0,
    itemNumber: "9",
    available: true,
    images: [imgProd1, imgProd2, imgProd3],
  },
};

function AgregarCarrito() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(0);

  const frequentPurchases = [
    {
      id: 2,
      name: "Arroz Blanco",
      price: 25.3,
      available: true,
      offer: true,
      image: imgProd1,
    },
    {
      id: 5,
      name: "Huevos de Granja AAA x12",
      price: 40.0,
      itemNumber: "5",
      available: true,
      images: [imgProd1, imgProd2, imgProd3],
    },
    {
      id: 8,
      name: "Yogurt Natural 500ml",
      price: 25.0,
      itemNumber: "8",
      available: true,
      images: [imgProd1, imgProd2, imgProd3],
    },
  ];

  useEffect(() => {
    const productData = productsDB[id];
    if (productData) {
      setProduct(productData);
    } else {
      navigate("/");
    }
  }, [id, navigate]);

  const incrementQuantity = () => setQuantity(quantity + 1);
  const decrementQuantity = () => {
    if (quantity > 1) setQuantity(quantity - 1);
  };

  const handleFrequentProductClick = (productId) => {
    navigate(`/producto/${productId}`);
  };

  if (!product) {
    return (
      <div className="loading-container">
        <div className="loading-text">Cargando producto...</div>
      </div>
    );
  }

  return (
    <div className="main-container">
      <div className="content-wrapper">
        <div className="sidebar">
          <div className="sidebar-content">
            <h2 className="sidebar-title">Compras Frecuentes</h2>

            <div className="frequent-products">
              {frequentPurchases.map((item, index) => (
                <div
                  key={index}
                  className="product-card"
                  onClick={() => handleFrequentProductClick(item.id)}
                >
                  <div className="product-header">
                    {item.offer && <span className="offer-badge">Oferta</span>}
                    <div className="rating">
                      <span className="star filled">★</span>
                      <span className="star filled">★</span>
                      <span className="star empty">☆</span>
                    </div>
                  </div>

                  <div className="product-image-container">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="product-image"
                    />
                  </div>

                  <div className="product-info">
                    <h3 className="product-name">{item.name}</h3>
                    <div className="availability-container">
                      <span
                        className={`availability ${
                          item.available ? "available" : "unavailable"
                        }`}
                      >
                        {item.available ? "DISPONIBLE" : "NO DISPONIBLE"}
                      </span>
                    </div>
                    <p className="product-price">L. {item.price}P/Kilo</p>

                    <button
                      className="add-button"
                      onClick={(e) => {
                        e.stopPropagation();
                        console.log(`Agregando ${item.name} al carrito`);
                        alert(`¡${item.name} agregado al carrito!`);
                      }}
                    >
                      Agregar
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="main-product">
          <div className="main-product-content">
            <div className="product-layout">
              <div className="product-images">
                <div className="main-image-container">
                  <img
                    src={product.images[selectedImage]}
                    alt={product.name}
                    className="main-image"
                  />
                </div>
                <div className="thumbnail-container">
                  {product.images.map((_, index) => (
                    <button
                      key={index}
                      onClick={() => setSelectedImage(index)}
                      className={`thumbnail ${
                        selectedImage === index ? "selected" : ""
                      }`}
                    >
                      <img
                        src={product.images[index]}
                        alt={`Vista ${index + 1}`}
                        className="thumbnail-image"
                      />
                    </button>
                  ))}
                </div>
              </div>

              <div className="product-details">
                <h1 className="product-title">{product.name}</h1>
                <p className="item-number">Número Item: {product.itemNumber}</p>

                <div className="price-section">
                  <span
                    className={`product-availability ${
                      product.available ? "available" : "unavailable"
                    }`}
                  >
                    {product.available ? "DISPONIBLE" : "NO DISPONIBLE"}
                  </span>
                  <div className="price-display">
                    L. {product.price.toFixed(2)}{" "}
                    <span className="price-unit">P/Kilo</span>
                  </div>
                </div>

                <div className="actions-container">
                  <div className="quantity-controls">
                    <button
                      onClick={decrementQuantity}
                      className="quantity-button"
                    >
                      <Minus size={20} />
                    </button>
                    <span className="quantity-display">{quantity}</span>
                    <button
                      onClick={incrementQuantity}
                      className="quantity-button"
                    >
                      <Plus size={20} />
                    </button>
                  </div>

                  <button
                    className="add-to-cart-button"
                    onClick={() => {
                      console.log(
                        `Agregando ${quantity}x ${product.name} al carrito`
                      );
                      alert(
                        `¡${quantity}x ${product.name} agregado al carrito!`
                      );
                    }}
                  >
                    AGREGAR AL CARRITO
                  </button>

                  <button
                    className="favorites-button"
                    onClick={() => {
                      console.log(
                        `Producto ${product.name} agregado a favoritos`
                      );
                      alert(`¡${product.name} agregado a favoritos!`);
                    }}
                  >
                    <Heart size={24} fill="white" />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AgregarCarrito;
